module testbench_top_module;

    // Testbench variables
    reg clk;
    reg L;
    reg [2:0] q_in;
    reg [2:0] r_in;
    wire [2:0] Q;

    // Instantiate Device Under Test
    top_module DUT (
        .clk(clk),
        .L(L),
        .q_in(q_in),
        .r_in(r_in),
        .Q(Q)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock period of 10 ns
    end

    integer i; // Loop variable

    // Stimulus
    initial begin
        // Initialize inputs
        q_in = 3'b000;
        r_in = 3'b000;
        L = 0;

        // Test Case 1: Check load functionality
        #10; // wait for the next positive edge
        r_in = 3'b101; L = 1;
        #10; // wait for the next positive edge
        if (Q !== 3'b101) $display("Test Case 1 Failed");

        #10; // wait for the next positive edge
        r_in = 3'b110; L = 1;
        #10; // wait for the next positive edge
        if (Q !== 3'b110) $display("Test Case 2 Failed");

        // Test Case 2: Check shift functionality
        #10; // wait for the next positive edge
        L = 0;
        q_in = Q;
        #10; // delay to allow action
        if (Q !== {q_in[1] ^ q_in[2], q_in[0], q_in[2]}) $display("Test Case 3 Failed");

        // Check edge cases
        L = 1; r_in = 3'b111; // All ones
        #10; // Apply load
        L = 0; q_in = Q; // Keep previous loaded value
        #10;
        if (Q !== {q_in[1] ^ q_in[2], q_in[0], q_in[2]}) $display("Test Case 4 Failed");

        L = 1; r_in = 3'b000; // All zeros
        #10; // Apply load
        L = 0; q_in = Q; // Keep previous loaded value
        #10;
        if (Q !== {q_in[1] ^ q_in[2], q_in[0], q_in[2]}) $display("Test Case 5 Failed");

        // All test cases
        for (i = 0; i < 8; i = i + 1) begin
            L = 1;
            r_in = i;
            #10;
            L = 0;
            q_in = Q;
            #10;
            if (Q !== {q_in[1] ^ q_in[2], q_in[0], q_in[2]}) begin
                $display("Test Case Failed at input pattern %b", i);
                $finish;
            end
        end

        #100;
        $display("all test cases passed");
        $finish;
    end

endmodule

