module testbench;
    // Declare inputs as reg and outputs as wire
    reg a, b, sel_b1, sel_b2;
    wire out_assign;
    wire out_always;

    // Instantiate the Device Under Test (DUT)
    top_module dut(
        .a(a),
        .b(b),
        .sel_b1(sel_b1),
        .sel_b2(sel_b2),
        .out_assign(out_assign),
        .out_always(out_always)
    );

    // Initial block starts here
    initial begin
        // Initialize all inputs
        a = 0; b = 0; sel_b1 = 0; sel_b2 = 0;

        // Apply test vectors
        test_case(0, 0, 0, 0);
        test_case(0, 0, 0, 1);
        test_case(0, 0, 1, 0);
        test_case(0, 1, 1, 1);
        test_case(1, 0, 0, 0);
        test_case(1, 0, 0, 1);
        test_case(1, 0, 1, 0);
        test_case(1, 0, 1, 1);

        // Finish the simulation
        #10;
        $finish;
    end

    integer passed_tests = 0;
    integer total_tests = 8;

    task test_case;
        input a_in, b_in, sel1, sel2;
        reg expected_out;
        begin
            a = a_in; b = b_in; sel_b1 = sel1; sel_b2 = sel2;

            // Determine expected output
            expected_out = (sel_b1 & sel_b2) ? b_in : a_in;

            // Wait for changes to propagate
            #1;

            if (out_assign != expected_out || out_always != expected_out) begin
                $display("Test failed: a=%b, b=%b, sel_b1=%b, sel_b2=%b, Expected=%b, Got out_assign=%b, out_always=%b", 
                         a, b, sel_b1, sel_b2, expected_out, out_assign, out_always);
            end else begin
                passed_tests = passed_tests + 1;
            end
        end
    endtask

    // Check results once all tests have been performed
    initial begin
        #90; // Wait until all tests are complete
        if (passed_tests == total_tests) begin
            $display("all test cases passed");
        end
    end
endmodule

