    reg [7:0] in;
    reg reset;
    wire done;

    // Instantiate the Device Under Test (DUT)
    top_module DUT (
        .clk(clk),
        .in(in),
        .reset(reset),
        .done(done)
    );

    // Clock generation
    always begin
        #5 clk = ~clk; // Toggle clock every 5 ns
    end

    // Stimuli: apply test vectors
    initial begin
        $display("Simulating...");
        clk = 0;
        reset = 1; //Assert reset
        #10 reset = 0;

        // Test Case 1: Proper sequence identification
        @(negedge clk) in = 8'b1000_0000; // Start of a message
        @(negedge clk) in = 8'b0000_1111;
        @(negedge clk) in = 8'b0111_0000;
        @(negedge clk)
        if (done !== 1'b1) begin
            $display("Test Case 1 Failed");
            $finish;
        end

        // Test Case 2: False start
        @(negedge clk) in = 8'b0110_0000; // Not a start of a message
        @(negedge clk) in = 8'b1000_1111; // Start of another message
        @(negedge clk) in = 8'b0000_1111; 
        @(negedge clk) in = 8'b0111_0000;
        @(negedge clk)
        if (done !== 1'b1) begin
            $display("Test Case 2 Failed");
            $finish;
        end

        // Test Case 3: Check reset functionality
        @(negedge clk) in = 8'b1000_0000; // Start of a message
        @(negedge clk) reset = 1; // Reset DUT in the middle
        @(negedge clk) reset = 0;
        @(negedge clk) in = 8'b0111_0000;
        @(negedge clk) in = 8'b0000_1111;
        @(negedge clk)
        if (done !== 1'b0) begin
            $display("Test Case 3 Failed");
            $finish;
        end

        // All test cases passed
        $display("all test cases passed");
        $finish;
    end
endmodule

