module tb_top_module;

    // Inputs
    reg clk;
    reg reset;

    // Outputs
    wire shift_ena;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .clk(clk),
        .reset(reset),
        .shift_ena(shift_ena)
    );

    initial begin
        // Initialize Inputs
        clk = 0;
        reset = 0;

        // Monitor changes in the signals
        $monitor("Time=%g, clk=%b, reset=%b, shift_ena=%b", $time, clk, reset, shift_ena);

        // Reset
        #10 reset = 1;  // Assert reset
        #10 reset = 0;  // Deassert reset

        // Wait for 100 ns for global reset to finish
        #100;

        // Test shift_ena is high for exactly 4 clock cycles after reset
        #20 if (shift_ena !== 1) begin
            $display("Test 1 Failed: shift_ena not asserted on first posedge after reset");
        end

        #10 if (shift_ena !== 1) begin
            $display("Test 2 Failed: shift_ena not asserted on second posedge after reset");
        end

        #10 if (shift_ena !== 1) begin
            $display("Test 3 Failed: shift_ena not asserted on third posedge after reset");
        end

        #10 if (shift_ena !== 1) begin
            $display("Test 4 Failed: shift_ena not asserted on fourth posedge after reset");
        end

        #10 if (shift_ena !== 0) begin
            $display("Test 5 Failed: shift_ena not deasserted on fifth posedge after reset");
        end

        // Additional check after multiple cycles to ensure it remains low
        #50 if (shift_ena !== 0) begin
            $display("Test 6 Failed: shift_ena not remaining low after initial 4 cycles");
        end
        else begin
            $display("all test cases passed");
        end

        // Finish simulation
        #100 $finish;
    end

    // Clock generation
    always #5 clk = ~clk;

endmodule

