module testbench;
    reg clk;
    reg reset;
    wire [3:0] q;
    integer i;
    reg [3:0] expected_q;
    reg error_detected;

    // Instantiate the Device Under Test (DUT)
    top_module DUT (
        .clk(clk),
        .reset(reset),
        .q(q)
    );

    // Generate clock with 10ns period
    initial begin
        clk = 0;
        forever #5 clk = ~clk;
    end

    // Test procedure
    initial begin
        reset = 0;
        error_detected = 0;

        // Assert reset to synchronous reset the counter
        @(posedge clk)
            reset = 1;
        @(posedge clk)
            reset = 0;

        // Expect q to be 0 after reset
        if (q !== 0) begin
            $display("Reset test failed at %t", $time);
            error_detected = 1;
        end

        // Verify counter counts correctly from 0 to 15
        for (i = 0; i < 16; i++) begin
            expected_q = i[3:0];
            @(posedge clk); // Wait for the next clock edge
            if (q !== expected_q) begin
                $display("Count test failed at %t, expected %d, got %d", $time, expected_q, q);
                error_detected = 1;
            end
        end

        // Check if counter rolls over to 0
        @(posedge clk);
        if (q !== 0) begin
            $display("Rollover test failed at %t, expected 0, got %d", $time, q);
            error_detected = 1;
        end

        // If no errors were detected, display that all test cases passed
        if (!error_detected)
            $display("all test cases passed");

        // Stop simulation
        $finish;
    end

endmodule

