        .q(q)
    );

    // Initialize inputs and variables
    reg [4:0] expected_q;
    reg correct;  // flag to indicate if all tests pass

    initial begin
        // Initialize inputs
        a = 0; b = 0; c = 0; d = 0;
        expected_q = 5'b00000;  // The 5-bit expected output values
        correct = 1'b1;         // initially assume all will pass

        // Apply stimulus
        #5; // at time 5ns
        #5;
        #5;
        #5;
        #5;  check_output;
        #5;  check_output;
        #5;  check_output;
        #5;  check_output;
        #5;  d = 1; check_output;
        #5;  d = 0; c = 1; check_output;
        #5;  d = 1; check_output;
        #5;  d = 0; b = 1; c = 0; check_output;
        #5;  d = 1; check_output;
        #5;  c = 1; d = 0; check_output;
        #5;  d = 1; check_output;
        #5;  a = 1; b = 0; c = 0; d = 0; check_output;
        #5;  d = 1; check_output;
        #5;  d = 0; c = 1; check_output;
        #5;  d = 1; check_output;
        #5;  d = 0; b = 1; c = 0; check_output;
        #5;  d = 1; check_output;
        #5;  d = 0; c = 1; check_output;
        #5;  d = 1; check_output;

        // Final check to display results
        if (correct) begin
            $display("all test cases passed");
        end

        // Finish simulation
        $finish;
    end

    task check_output;
        begin
            #5;
            case({a, b, c, d})
                4'b0000, 4'b0100, 4'b0010, 4'b1010, 4'b1000, 4'b1100 :
                    expected_q = 0;
                4'b0001, 4'b0101, 4'b0110, 4'b0111, 4'b1001, 4'b1011, 4'b1101, 4'b1110, 4'b1111: 
                    expected_q = 1;
                default:
                    expected_q = 0;
            endcase

