module tb_top_module;

  reg clk;
  reg [7:0] in;
  reg reset;
  wire done;

  top_module UUT (
    .clk(clk),
    .in(in),
    .reset(reset),
    .done(done)
  );

  // Clock generation
  initial begin
    clk = 1'b0;
    forever #5 clk = ~clk;  // Clock period of 10ns
  end

  // Test vector
  initial begin
    reset = 1;
    in = 8'h00;
    #20;   // Hold reset for 20ns

    reset = 0;
    #10;   // Start testing after reset deasserted

    // Test sequence 1: Proper message start
    in = 8'b1000_0000; // Start of a message, in[3] = 1
    #10;
    in = 8'b0111_1111; // Second byte of the message
    #10;
    in = 8'b0000_1111; // Third byte, expect done here
    #10;
    if (done !== 1'b1) begin
      $display("Test case failed");
      $finish;
    end

    // Test interruption by another start byte
    in = 8'b1000_0000; // New message start before completing another
    #10;
    in = 8'b1000_0001; // Another start byte, should reset the count
    #10;
    in = 8'b0101_0101;
    #10;
    in = 8'b0011_0011; // Should signal done
    #10;
    if (done !== 1'b1) begin
      $display("Test case failed");
      $finish;
    end

    // Test with more random inputs
    in = 8'b0101_0101;
    #10;
    in = 8'b1000_0100; // Start new message
    #10;
    in = 8'b0110_0110;
    #10;
    in = 8'b0010_0010; // Should signal done
    #10;
    if (done !== 1'b1) begin
      $display("Test case failed");
      $finish;
    end

    // Reset in the middle of transmission
    in = 8'b1000_1001; // Start of a message
    #10;
    in = 8'b0110_0110;
    #5;
    reset = 1;  // Reset
    #10;
    reset = 0;
    in = 8'b0011_0011; // This should not cause done since it was reset
    #10;
    if (done !== 1'b0) begin
      $display("Test case failed");
      $finish;
    end

    $display("all test cases passed");
  end

endmodule

