    reg clk, reset, data, ack;
    wire [3:0] count;
    reg counting, done;

    top_module dut (
        .clk(clk),
        .reset(reset),
        .data(data),
        .count(count),
        .counting(counting),
        .done(done),
        .ack(ack)
    );

    // Clock generation
    initial begin
        clk = 1'b0;
        forever #5 clk = ~clk; // 100 MHz clock (10 ns period, 5 ns half-period)
    end

    // Stimulus
    initial begin
        // Initialize inputs
        reset = 1;
        ack = 0;
        data = 0;
        #15;
        reset = 0;
        #10;

        // Test start detection and counting
        send_bits(4'b1101);  // Start pattern
        send_bits(4'b0101);  // delay = 5 (6000 cycles)
        ack_done_sequence(6000);
        #100;

        send_bits(4'b1101);  // Start pattern
        send_bits(4'b0000);  // delay = 0 (1000 cycles)
        ack_done_sequence(1000);
        #100;

        send_bits(4'b1101);  // Start pattern
        send_bits(4'b1111);  // delay = 15 (16000 cycles)
        ack_done_sequence(16000);
        #100;

        // Simulation end
        $finish;
    end

    // Task to send bits through the data input
    task send_bits;
        input [3:0] bits;
        integer i;
        begin
            for (i = 3; i >= 0; i = i - 1) begin
                data = bits[i];
                #10;  // Duration between bits
            end
        end
    endtask

    // Task to acknowledge, check done signal and counting
    task ack_done_sequence;
        input integer count_cycles;
        integer i;
        begin
            for (i = 0; i < count_cycles / 1000; i = i + 1) begin
                if (done) $display("Failure: Done asserted early.");
                #1000;
            end
            if (!done) $display("Failure: Done not asserted as expected.");
            ack = 1; // Acknowledge the timer
            #10;
            ack = 0;
        end
    endtask

    integer passed_cases = 0, total_cases = 3;

    always @(posedge clk) begin
       // Check if counting is as expected
        // (to be implemented based on the actual counting implementation in the DUT)
    end

    initial begin
        #70000;
        if (passed_cases == total_cases)
            $display("all test cases passed");
    end
endmodule

