    reg clk;
    reg areset;
    reg load;
    reg ena;
    reg [3:0] data;
    wire [3:0] q;

    top_module DUT (
        .clk(clk),
        .areset(areset),
        .load(load),
        .ena(ena),
        .data(data),
        .q(q)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Generate clock with period of 10ns
    end

    // Test cases
    initial begin
        // Initialize inputs
        areset = 0; load = 0; ena = 0; data = 0;

        // Reset the shift register
        #5;
        areset = 1;
        #10;
        areset = 0;
        if (q !== 4'b0000) begin
            $display("Test case failed: Reset functionality");
            $finish;
        end

        // Load data into the shift register
        #5;
        load = 1;
        data = 4'b1010;
        #10;
        if (q !== 4'b1010) begin
            $display("Test case failed: Load functionality");
            $finish;
        end

        // Enable shift
        #5;
        load = 0;
        ena = 1;
        #10;
        if (q !== 4'b0101) begin
            $display("Test case failed: Shift right functionality");
            $finish;
        end

        // Check load priority over enable
        #5;
        ena = 1; load = 1;
        data = 4'b1100;
        #10;
        if (q !== 4'b1100) begin
            $display("Test case failed: Load priority over enable");
            $finish;
        end

        // Additional shift with enable only
        #5;
        load = 0;
        #10;
        if (q !== 4'b0110) begin
            $display("Test case failed: Additional shift right");
            $finish;
        end

        // Final check: reset after some operations
        #5;
        areset = 1;
        #10;
        if (q !== 4'b0000) begin
            $display("Test case failed: Final reset");
            $finish;
        end

