module testbench;
    reg a, b, c;
    wire out;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .a(a),
        .b(b),
        .c(c),
        .out(out)
    );

    // Test variables
    integer errors = 0;
    reg expected_out;

    // Procedure to apply test vector
    task apply_test_vector;
        input reg a_val, b_val, c_val, expected_val;
        begin
            a = a_val; b = b_val; c = c_val;
            expected_out = expected_val;
            #10; // Wait for the values to propagate
            if (out !== expected_out) begin
                errors = errors + 1;
                $display("Test case failed for a=%b, b=%b, c=%b: Expected out=%b, Got out=%b", a, b, c, expected_out, out);
            end
        end
    endtask

    // Test sequence
    initial begin
        // Initialize inputs
        a = 0; b = 0; c = 0;

        // Apply test cases based on the Karnaugh map
        apply_test_vector(0, 0, 0, 0); // a=0, b=0, c=0  --> out=0
        apply_test_vector(0, 0, 1, 1); // a=0, b=0, c=1  --> out=1
        apply_test_vector(0, 1, 0, 1); // a=0, b=1, c=0  --> out=1
        apply_test_vector(0, 1, 1, 1); // a=0, b=1, c=1  --> out=1
        apply_test_vector(1, 0, 0, 1); // a=1, b=0, c=0  --> out=1
        apply_test_vector(1, 0, 1, 1); // a=1, b=0, c=1  --> out=1
        apply_test_vector(1, 1, 0, 1); // a=1, b=1, c=0  --> out=1
        apply_test_vector(1, 1, 1, 1); // a=1, b=1, c=1  --> out=1

        // Check the result and display the appropriate message
        #10; // Allow time for the last propagation
        if (errors == 0)
            $display("all test cases passed");

        // Finish the simulation
        $finish;
    end
