module testbench;
    reg clk, areset, bump_left, bump_right, ground, dig;
    wire walk_left, walk_right, aaah, digging;

    // Instantiate the Device Under Test (DUT)
    top_module DUT (
        .clk(clk),
        .areset(areset),
        .bump_left(bump_left),
        .bump_right(bump_right),
        .ground(ground),
        .dig(dig),
        .walk_left(walk_left),
        .walk_right(walk_right),
        .aaah(aaah),
        .digging(digging)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;
    end

    // Test cases
    initial begin
        areset = 1; #10;
        areset = 0; #10;

        // Check Reset Behavior - Should start walking left
        if (walk_left !== 1'b1 || walk_right !== 1'b0) begin
            $display("Reset behavior test failed");
            $finish;
        end

        // Ground disappears (Lemming falls)
        ground = 0; #10;
        if (aaah !== 1) begin
            $display("Fall without ground test failed");
            $finish;
        end

        // Ground reappears (Continue in the same direction)
        ground = 1; #10;
        if (walk_left !== 1'b1 || aaah !== 1'b0) begin
            $display("Resume walking after fall test failed");
            $finish;
        end

        // Bump on the Right
        bump_right = 1; #10; bump_right = 0; #10;
        if (walk_right !== 1'b1 || walk_left !== 1'b0) begin
            $display("Switch to walking right test failed");
            $finish;
        end

        // Starts digging
        dig = 1; #20; dig = 0; #10;
        if (digging !== 1'b1) begin
            $display("Start digging test failed");
            $finish;
        end

        // Digging continues until no ground
        ground = 0; #10;
        if (aaah !== 1 || digging !== 1'b0) begin
            $display("Stop digging because of no ground test failed");
            $finish;
        end

        // Resume walking right after hitting ground again
        ground = 1; #10;
        if (walk_right !== 1'b1) begin
            $display("Resume walking right after digging and falling test failed");
            $finish;
        end

        // Test passed all cases
        $display("all test cases passed");
        $finish;
    end
endmodule

