    top_module dut(
        .clk(clk),
        .in(in),
        .pedge(pedge)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // 100 MHz Clock 
    end

    // Initialize inputs
    initial begin
        in = 8'b00000000;
        previous_in = 8'b00000000;
    end

    // Check output against expected result
    initial begin
        // Initial delay
        #10;

        // Test vector 1: No edges
        in = 8'b00000000;
        #10;
        check_edges();

        // Test vector 2: Single edge in bit 0
        in = 8'b00000001;
        #10;
        check_edges();

        // Test vector 3: Multiple edges
        in = 8'b11001100;
        #10;
        check_edges();

        // Test vector 4: All edges
        in = 8'b11111111;
        #10;
        check_edges();

        // Test Vector 5: Edges back to 0
        in = 8'b00000000;
        #10;
        check_edges();

        // Test Vector 6: Random edges
        in = 8'b10101010;
        #10;
        check_edges();

        in = 8'b11110000;
        #10;
        check_edges();

        // Check if all tests passed
        if (test_passed)
            $display("all test cases passed");

        // End simulation     
        $finish;
    end

    reg test_passed = 1'b1;

    task check_edges;
        begin
            for (i = 0; i < 8; i = i + 1) begin
                if ((previous_in[i] === 0 && in[i] === 1 && pedge[i] !== 1) ||
                    (previous_in[i] !== 0 && in[i] !== 1 && pedge[i] !== 0)) begin
                    test_passed = 1'b0;
                end
            end
            previous_in = in;
