module testbench;

    reg clk, in, reset;
    wire out;
    integer errors;

    // Instantiate the Device Under Test (DUT)
    top_module dut(
        .clk(clk),
        .in(in),
        .reset(reset),
        .out(out)
    );

    // Clock generation
    always begin
        clk = 1; #5; // High for 5ns
        clk = 0; #5; // Low for 5ns (10ns period)
    end

    // Stimuli: applying test vectors
    initial begin
        errors = 0;
        reset = 1; // Apply reset
        in = 0; #10;  // Hold reset for one clock cycle
        reset = 0; #10;

        // Test Case 1: B (out=1) --in=0--> A
        in = 0;
        #10;
        if (out !== 1) begin
            errors = errors + 1;
        end

        #10; // wait to transition to next state
        if (out !== 0) begin
            errors = errors + 1;
        end

        // Test Case 2: B (out=1) --in=1--> B
        reset = 1; in = 0; #10;
        reset = 0; in = 1; #10;
        #10;
        if (out !== 1) begin
            errors = errors + 1;
        end

        #10; // stay at current state
        if (out !== 1) begin
            errors = errors + 1;
        end

        // Test Case 3: A (out=0) --in=0--> B
        reset = 1; in = 0; #10;
        reset = 0; in = 0; #10; // bring the system to state A (out should be 0)
        #10; in = 0; #10; // change to state B
        if (out !== 1) begin
            errors = errors + 1;
        end

        // Test Case 4: A (out=0) --in=1--> A
        reset = 1; in = 0; #10;
        reset = 0; in = 0; #10; // bring the system to state A first
        #10; in = 1; #10; // stay at state A
        if (out !== 0) begin
            errors = errors + 1;
        end

        // Final status check
        #10;
        if (errors == 0) begin
            $display("all test cases passed");
        end

        $finish;
    end

endmodule

