    top_module dut (
        .a(a), 
        .b(b), 
        .c(c), 
        .d(d),
        .out(out)
    );

    initial begin
        // Initialize test vectors based on Karnaugh map
        test_vector[0] = 4'b0000; // d: don't-care for output
        test_vector[1] = 4'b0001; // d: don't-care for output
        test_vector[2] = 4'b0010; // Output should be 1
        test_vector[3] = 4'b0011; // Output should be 1
        test_vector[4] = 4'b0100; // Output should be 0
        test_vector[5] = 4'b0101; // Output should be 0
        test_vector[6] = 4'b0110; // d: don't-care for output
        test_vector[7] = 4'b0111; // d: don't-care for output
        test_vector[8] = 4'b1000; // Output should be 0
        test_vector[9] = 4'b1001; // Output should be 1
        test_vector[10] = 4'b1010; // Output should be 1
        test_vector[11] = 4'b1011; // Output should be 1
        test_vector[12] = 4'b1100; // Output should be 0
        test_vector[13] = 4'b1101; // Output should be 1
        test_vector[14] = 4'b1110; // Output should be 1
        test_vector[15] = 4'b1111; // Output should be 1

        pass = 1;

        for (i = 0; i < 16; i = i + 1) begin
            {a, b, c, d} = test_vector[i][3:0];
            #10; // wait for output to stabilize
            // Check output against expected results using non-don't-care cases
            case(test_vector[i])
                // Non don't-care cases
                4'b0010: if(out !== 1) pass = 0;
                4'b0011: if(out !== 1) pass = 0;
                4'b0100: if(out !== 0) pass = 0;
                4'b0101: if(out !== 0) pass = 0;
                4'b1000: if(out !== 0) pass = 0;
                4'b1001: if(out !== 1) pass = 0;
                4'b1010: if(out !== 1) pass = 0;
                4'b1011: if(out !== 1) pass = 0;
                4'b1100: if(out !== 0) pass = 0;
                4'b1101: if(out !== 1) pass = 0;
                4'b1110: if(out !== 1) pass = 0;
                4'b1111: if(out !== 1) pass = 0;
            endcase
        end

        if (pass) begin
            $display("all test cases passed");
        end
        $finish;
    end

