module testbench;
    reg clk, j, k, reset;
    wire out;

    // Instantiate the DUT
    top_module dut (
        .clk(clk),
        .j(j),
        .k(k),
        .reset(reset),
        .out(out)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // Clock period of 10 ns
    end

    // Test cases
    initial begin
        // Initialize the inputs
        j = 0; k = 0; reset = 0;
        #10;  // Wait for the initial reset

        // Apply reset
        reset = 1; #10; reset = 0; #10;
        if (out !== 0) $display("Test 1 Failed. When reset, output should be 0.");

        // State OFF, j = 0
        j = 0; k = 0; #20;
        if (out !== 0) $display("Test 2 Failed. OFF, j = 0 should remain OFF (out = 0).");

        // State OFF, j = 1 -> Transition to ON
        j = 1; #10;
        if (out !== 1) $display("Test 3 Failed. OFF, j = 1 should turn ON (out = 1).");

        // State ON, k = 0
        j = 0; k = 0; #20;
        if (out !== 1) $display("Test 4 Failed. ON, k = 0 should remain ON (out = 1).");

        // State ON, k = 1 -> Transition to OFF
        k = 1; #10;
        if (out !== 0) $display("Test 5 Failed. ON, k = 1 should turn OFF (out = 0).");

        // Final test back to OFF, j = 0
        j = 0; k = 0; #20;
        if (out !== 0) $display("Test 6 Failed. Should be OFF (out = 0).");

        // if all tests passed, print message
        if (get_test_failed_flag() == 0) begin
            $display("All test cases passed");
        end

        $finish;
    end

    // Function to check if any test has failed
    function integer get_test_failed_flag;
        integer file;
        get_test_failed_flag = 0;
        file = $fopen("test.log");
        if (file != 0) begin
            get_test_failed_flag = 1;
            $fclose(file);
        end
    endfunction
endmodule

