module testbench;
    // Input signals
    reg d;
    reg done_counting;
    reg ack;
    reg [9:0] state;

    // Output signals
    wire B3_next;
    wire S_next;
    wire S1_next;
    wire Count_next;
    wire Wait_next;
    wire done;
    wire counting;
    wire shift_ena;

    // Instantiate the Device Under Test (DUT)
    top_module DUT (
        .d(d),
        .done_counting(done_counting),
        .ack(ack),
        .state(state),
        .B3_next(B3_next),
        .S_next(S_next),
        .S1_next(S1_next),
        .Count_next(Count_next),
        .Wait_next(Wait_next),
        .done(done),
        .counting(counting),
        .shift_ena(shift_ena)
    );

    // Test vectors
    task perform_test;
        input d_in;
        input done_counting_in;
        input ack_in;
        input [9:0] state_in;
        input [9:0] expected_state_out;
        input done_expected;
        input counting_expected;
        input shift_ena_expected;
        begin
            d = d_in;
            done_counting = done_counting_in;
            ack = ack_in;
            state = state_in;
            #10; // Wait some time for outputs to stabilize

            if (!(B3_next === expected_state_out[0] &&
                  S_next === expected_state_out[1] &&
                  S1_next === expected_state_out[2] &&
                  Count_next === expected_state_out[3] &&
                  Wait_next === expected_state_out[4] &&
                  done === done_expected &&
                  counting === counting_expected &&
                  shift_ena === shift_ena_expected)) begin
                $display("Test failed for input combination: d=%b, done_counting=%b, ack=%b, state=%b", d_in, done_counting_in, ack_in, state_in);
            end
        end
    endtask

    // Test cases
    initial begin
        // Initialize inputs
        d = 0;
        done_counting = 0;
        ack = 0;
        state = 10'b0000000000; // None set initially

        // Reset sequence
        #10 state = 10'b0000000001; // Start at S

        // Simulation test cases
        perform_test(0, 0, 0, 10'b0000000001, 10'b0000000001, 0, 0, 0); // S -> S
        perform_test(1, 0, 0, 10'b0000000010, 10'b0000000100, 0, 0, 0); // S1 -> S11
        perform_test(0, 0, 0, 10'b0000000100, 10'b0000001000, 0, 0, 0); // S11 -> S110
        perform_test(1, 0, 0, 10'b0000001000, 10'b0000010000, 0, 0, 1); // S110 -> B0
        perform_test(0, 0, 0, 10'b0000100000, 10'b0001000000, 0, 1, 0); // Count -> Wait (not done)
        perform_test(0, 1, 0, 10'b0001000000, 10'b0000000001, 0, 0, 0); // Wait -> S (ack=1)

        // Check if all tests passed
        #50 $display("all test cases passed");
    end

endmodule

