module top_module_tb;

    // Inputs to the DUT
    reg clk;
    reg areset;
    reg bump_left;
    reg bump_right;
    reg ground;
    reg dig;

    // Outputs from the DUT
    wire walk_left;
    wire walk_right;
    wire aaah;
    wire digging;

    // Instantiate the DUT
    top_module dut(
        .clk(clk),
        .areset(areset),
        .bump_left(bump_left),
        .bump_right(bump_right),
        .ground(ground),
        .dig(dig),
        .walk_left(walk_left),
        .walk_right(walk_right),
        .aaah(aaah),
        .digging(digging)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // 100 MHz Clock
    end

    // Testbench signals for verification
    integer errors = 0;

    // Test sequence
    initial begin
        // Initialize Inputs
        areset = 1;  // Start with a reset
        bump_left = 0;
        bump_right = 0;
        ground = 1;
        dig = 0;

        // Wait for reset
        @(posedge clk);
        #1 areset = 0;

        // Normal walking, check initial state is walking left
        @(posedge clk);
        if (walk_left !== 1 || walk_right !== 0) begin
            $display("Fail: Initial state should be walking left");
            errors = errors + 1;
        end

        // Test bumping left while walking left
        bump_left = 1;
        @(posedge clk);
        if (walk_left !== 0 || walk_right !== 1) begin
            $display("Fail: Should switch to walking right on bump left");
            errors = errors + 1;
        end
        bump_left = 0;

        // Test falling
        ground = 0;
        @(posedge clk);
        if (aaah !== 1) begin
            $display("Fail: Should be falling with 'aaah' high");
            errors = errors + 1;
        end
        repeat(21) @(posedge clk); // simulate longer fall
        ground = 1;  // hitting the ground after long fall
        @(posedge clk);
        // Check for splatter condition
        if (walk_left !== 0 && walk_right !== 0 && aaah !== 0 && digging !== 0) begin
            $display("Fail: Should splatter after long fall");
            errors = errors + 1;
        end

        // Reset uncertainty and check resuming in previous direction
        areset = 1; #1 areset = 0;
        @(posedge clk);
        bump_right = 1;
        @(posedge clk);
        bump_right = 0;
        dig = 1;
        @(posedge clk);
        if (digging !== 1 || aaah !== 0) begin
            $display("Fail: Should start digging not falling");
            errors = errors + 1;
        end

        // Check if all validations are passed
        if (errors == 0) begin
            $display("all test cases passed");
        end

        // Finish the simulation
        $finish;
    end

endmodule

