module tb_top_module;

    reg clk;
    reg [7:0] in;
    reg reset;
    wire [23:0] out_bytes;
    wire done;

    top_module DUT(
        .clk(clk),
        .in(in),
        .reset(reset),
        .out_bytes(out_bytes),
        .done(done)
    );

    initial begin
        clk = 0;
        forever #5 clk = ~clk;
    end

    initial begin
        reset = 1;
        in = 0;
        @(negedge clk); // 5ns clock edge
        @(posedge clk); // 10ns clock edge
        reset = 0;
        // Start sending inputs
        @(posedge clk) in = 8'h2C;
        @(posedge clk) in = 8'h81;
        @(posedge clk) in = 8'h09;
        @(posedge clk) in = 8'h6B;
        @(posedge clk) in = 8'h0D;
        @(posedge clk) in = 8'h8D;
        @(posedge clk) in = 8'h6D;
        @(posedge clk) in = 8'h12;
        @(posedge clk) in = 8'h01;
        @(posedge clk) in = 8'h76;
        @(posedge clk) in = 8'h3D;
        @(posedge clk) in = 8'hED;
        @(posedge clk) in = 8'h8C;
        @(posedge clk) in = 8'hF9;
        @(posedge clk) in = 8'hCE;
        @(posedge clk) in = 8'hC5;
        @(posedge clk) in = 8'hAA;
        // End of Test Data
        @(posedge clk) in = 0; 
        #100; 
        $finish;
    end

    reg [23:0] expected_output;
    reg [5:0] message_count;
    initial begin
        message_count = 0;
        @(negedge reset);
        expected_output = 0;
        repeat (19) @(posedge clk);
        expected_output = 24'h2c8109;
        message_count = message_count + 1;
        repeat (6) @(posedge clk);
        expected_output = 24'h6b0d8d;
        message_count = message_count + 1;
        repeat (6) @(posedge clk);
        expected_output = 24'h6d1201;
        message_count = message_count + 1;
        repeat (6) @(posedge clk);
        expected_output = 24'hd763d;
        message_count = message_count + 1;
        repeat (6) @(posedge clk);
        expected_output = 24'hed8cf9;
        message_count = message_count + 1;
        @(posedge clk);
        if (message_count == 5) $display("all test cases passed");
    end

    always @(posedge clk) begin
        if (done && out_bytes !== expected_output) begin
            $display("Test failed at time %t: Expected %h, Got %h", $time, expected_output, out_bytes);
        end
    end

endmodule

