    wire out_assign;
    wire out_always_comb;
    wire out_always_ff;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .clk(clk),
        .a(a),
        .b(b),
        .out_assign(out_assign),
        .out_always_comb(out_always_comb),
        .out_always_ff(out_always_ff)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // Clock period of 10ns
    end

    // Test cases
    initial begin
        // Initialize Inputs
        a = 0;
        b = 0;

        // Wait for global reset to finish
        #10;

        // Test input combinations
        a = 0; b = 0; #10;  // Test 0
        verify_outputs(0, 0, 0);

        a = 0; b = 1; #10;  // Test 1
        verify_outputs(1, 0, 1);

        a = 1; b = 0; #10;  // Test 2
        verify_outputs(2, 1, 0);

        a = 1; b = 1; #10;  // Test 3
        verify_outputs(3, 1, 0);

        // Check all tests passed
        if (test_passed) begin
            $display("All test cases passed.");
        end
        $finish;
    end

    // Variable to track test status
    reg test_passed = 1'b1;

    // Task to verify outputs
    task verify_outputs;
        input integer test_case;
        input expected_out_assign;
        input expected_out_ff;
        begin
            if ((out_assign !== expected_out_assign) || (out_always_comb !== expected_out_assign)) begin
                $display("Test case %d failed: expected assign and comb outputs %b, got %b and %b", test_case, expected_out_assign, out_assign, out_always_comb);
                test_passed = 1'b0;
            end
            @(posedge clk) // correct way to wait for a clock edge in a task
            if (out_always_ff !== expected_out_ff) begin
                $display("Test case %d failed: expected ff output %b, got %b", test_case, expected_out_ff, out_always_ff);
                test_passed = 1'b0;
            end
        end
    endtask
