module top_module_tb;

  reg clk;
  reg reset;
  reg data;
  wire shift_ena;
  wire counting;
  reg done_counting;
  wire done;
  reg ack;

  // Instantiate the DUT
  top_module DUT (
    .clk(clk),
    .reset(reset),
    .data(data),
    .shift_ena(shift_ena),
    .counting(counting),
    .done_counting(done_counting),
    .done(done),
    .ack(ack)
  );

  // Clock generation
  initial begin
    clk = 0;
    forever #5 clk = ~clk;  // Clock with period 10ns
  end

  // Test scenarios
  initial begin
    // Initialize inputs
    reset = 1; data = 0; ack = 0; done_counting = 0;
    #15;  // Wait for 15ns for system to stabilize
    reset = 0;

    // Test case 1: Start sequence detection
    #10 data = 1;  // Start bit
    #10 data = 1;
    #10 data = 0;
    #10 data = 1;  // Pattern 1101 detected

    // Test case 2: Check shift_ena after pattern
    #10 data = 0;  // Start shifting in 4 bits
    if (!(shift_ena && !counting && !done)) $display("Test case 2 failed");
    #10 data = 1; 
    if (!(shift_ena && !counting && !done)) $display("Test case 2 failed");
    #10 data = 0; 
    if (!(shift_ena && !counting && !done)) $display("Test case 2 failed");
    #10 data = 1; 
    if (!(shift_ena && !counting && !done)) $display("Test case 2 failed");

    // Test case 3: Check counting start
    #10;
    if (!(counting && !done)) $display("Test case 3 failed");

    // Test case 4: when counting is done
    #20 done_counting = 1;
    if (!done) $display("Test case 4 failed");

    // Test case 5: Reset after ack
    #10 ack = 1;
    #10;
    if (shift_ena || counting || done) $display("Test case 5 failed");

    // Additional sequence to check patterns and reset
    #10 ack = 0; done_counting = 0; data = 0;
    #10 data = 1;
    #10 data = 0;
    #10 data = 1;  // Trying to set another pattern: 101
    #10 data = 1;
    #10 if (done || shift_ena || counting) $display("Additional sequence failed");

    // All tests passed message
    if (!$test$plusargs("Test case failed")) $display("all test cases passed");

    $finish; // End simulation
  end

endmodule

