    wire Z;
    reg [7:0] input_sequence;
    integer i, error_count;

    // Instantiate the DUT
    top_module dut (
        .clk(clk),
        .enable(enable),
        .S(S),
        .A(A),
        .B(B),
        .C(C),
        .Z(Z)
    );

