    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // Clock period of 10ns
    end

    // Initialize and test logic
    initial begin
        error_count = 0;
        enable = 1;  // Enable shifting
        S = 0;

        // Input sequence to shift into the register
        input_sequence = 8'b10101010;  // Example pattern

        // Shift in the sequence
        for (i = 7; i >= 0; i = i - 1) begin
            S = input_sequence[i];
            #10;  // Wait for one clock cycle
        end

        enable = 0;  // Stop shifting

        // Test each memory cell output
        for (i = 0; i < 8; i = i + 1) begin
            {A, B, C} = i[2:0];  // Set ABC to select the corresponding Q
            #10;  // Wait for the address to take effect
            if (Z !== input_sequence[i]) begin
                $display("Test case failed for input location %d: expected %b, got %b", i, input_sequence[i], Z);
                error_count = error_count + 1;
            end
        end

        if (error_count == 0) begin
            $display("all test cases passed");
        end

        $finish;  // End simulation
    end
