    reg clk, w, R, E, L;
    wire Q;

    // Instantiate the DUT
    top_module dut (
        .clk(clk),
        .w(w),
        .R(R),
        .E(E),
        .L(L),
        .Q(Q)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // 100 MHz clock
    end

    // Test stimulus
    initial begin

        // Initialize inputs
        w = 0; R = 0; E = 0; L = 0;

        #10; // Wait for 10ns
        // Case 1: Load operation
        R = 1; L = 1; 
        #10;
        L = 0;

        // Case 2: Shift operation while enable is asserted
        E = 1; w = 1; 
        #10;

        // Case 3: Shift operation while enable is not asserted
        E = 0; w = 0;
        #10;

        // Case 4: Check reaction to toggling enable
        E = 1;
        #10;

        // Case 5: Much more toggling
        E = 0; w = 1; R = 0;
        #10;
        E = 1;
        #10;

        // Finalize tests
        $finish;
    end

    // Monitor & Check results for completion
    reg all_passed = 1'b1;
    reg [15:0] error_count = 0;

    always @(posedge clk) begin
        // Evaluation at each positive edge of clk
        // Adding delay to check result after edge
        #1;
        // Test conditions
        if (L && Q !== R) begin
            all_passed = 0; error_count = error_count + 1;
        end
        else if (E && !L && Q !== w) begin
            all_passed = 0; error_count = error_count + 1;
        end
    end

    // Display result
    initial begin
        #100; // Run for sufficient time; adjust as needed based on test case duration
        if (all_passed && error_count == 0) begin
            $display("all test cases passed");
        end
    end

