module testbench();
    reg clk, reset, w;
    wire z;

    // Instantiate the DUT
    top_module dut (
        .clk(clk),
        .reset(reset),
        .w(w),
        .z(z)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;
    end

    // Stimulus here
    initial begin
        reset = 1; w = 0;
        #10; reset = 0; // Release reset after some time

        // Following the state transitions given in the problem description

        // State A (output should be 0)
        test_state(0, 0); // A-->B
        test_state(1, 0); // A-->A

        // State B (output should be 0)
        test_state(0, 0); // B-->C
        test_state(1, 0); // B-->D

        // State C
        test_state(0, 0); // C-->E
        test_state(1, 0); // C-->D

        // State D
        test_state(0, 0); // D-->F
        test_state(1, 0); // D-->A

        // State E (output is 1)
        test_state(0, 1); // E-->E
        test_state(1, 1); // E-->D

        // State F (output is 1)
        test_state(0, 1); // F-->C
        test_state(1, 1); // F-->D

        // Check final state to ensure accuracy
        // Settle state then terminate
        #10; $finish;
    end

    integer total_tests = 0;
    integer passed_tests = 0;

    // Helper task to test state output and transitions
    task test_state;
        input w_val;
        input expected_z;

        begin
            w = w_val;
            #10; // Wait for state transition and settling

            total_tests = total_tests + 1;
            if (z === expected_z) begin
                passed_tests = passed_tests + 1;
            end
        end
    endtask

    // Evaluate the results at the end of simulation
    initial begin
        #500; // Wait for all tests to complete
        if (passed_tests == total_tests) begin
            $display("all test cases passed");
        end
    end
endmodule

