module testbench;
    reg clk;
    reg areset;
    reg train_valid;
    reg train_taken;
    wire [1:0] state;

    // Instantiate the DUT
    top_module DUT(
        .clk(clk),
        .areset(areset),
        .train_valid(train_valid),
        .train_taken(train_taken),
        .state(state)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // 100 MHz Clock
    end

    // Test cases
    integer i;
    integer errors = 0;

    initial begin
        // Initialize signals
        train_valid = 0;
        train_taken = 0;
        areset = 1;
        #10 areset = 0;

        // Reset test
        #20;
        if (state != 2'b01) begin
            $display("Reset test failed. state=%b", state);
            errors = errors + 1;
        end

        // Test incrementing
        train_valid = 1;
        train_taken = 1;
        // Should increment to 2
        #10;
        if (state != 2'b10) begin
            $display("Increment to 2 test failed. state=%b", state);
            errors = errors + 1;
        end

        // Increment to max (3)
        #10;
        if (state != 2'b11) begin
            $display("Increment to max (3) test failed. state=%b", state);
            errors = errors + 1;
        end

        // Trying to increment beyond max
        #10;
        if (state != 2'b11) begin
            $display("Increment beyond max test failed. state=%b", state);
            errors = errors + 1;
        end

        // Test decrementing
        train_taken = 0;
        // Should decrement to 2
        #10;
        if (state != 2'b10) begin
            $display("Decrement to 2 test failed. state=%b", state);
            errors = errors + 1;
        end

        // Decrement to min (0)
        #20;
        if (state != 2'b00) begin
            $display("Decrement to min (0) test failed. state=%b", state);
            errors = errors + 1;
        end

        // Trying to decrement beyond min
        #10;
        if (state != 2'b00) begin
            $display("Decrement beyond min test failed. state=%b", state);
            errors = errors + 1;
        end

        // Check hold value without valid
        train_valid = 0;
        #20;
        if (state != 2'b00) begin
            $display("Hold value test failed. state=%b", state);
            errors = errors + 1;
        end

        // Wrap up testing
        if (errors == 0) begin
            $display("all test cases passed");
        end

        // Finish simulation
        $finish;
    end
endmodule

