        end
    end

    // Case 2: Reload while counting
    load = 1;
    data = 10'd15; // Load the counter with 15
    #10;
    load = 0;
    #50; // Let it count a few cycles
    load = 1;
    data = 10'd10; // Reload with 10 before reaching 0
    #10;
    load = 0;
    for (i = 0; i < 10; i = i + 1) begin
        #10;
