module testbench;

    // Testbench Variables
    reg [2:0] a, b;
    wire [2:0] out_or_bitwise;
    wire out_or_logical;
    wire [5:0] out_not;
    integer i, j, error_flag;

    // Instantiate the DUT
    top_module dut(
        .a(a),
        .b(b),
        .out_or_bitwise(out_or_bitwise),
        .out_or_logical(out_or_logical),
        .out_not(out_not)
    );

    // Task to perform bitwise checks
    task check_outputs;
        reg [2:0] expected_bitwise;
        reg expected_logical;
        reg [5:0] expected_not;
        begin
            expected_bitwise = a | b;  // Bitwise OR of a and b
            expected_logical = (|a) || (|b);  // Logical OR of a and b
            expected_not = {~b, ~a};  // Inverted b concatenated with inverted a

            // Checking bitwise OR
            if (out_or_bitwise !== expected_bitwise) begin
                $display("Bitwise OR failed at a=%b, b=%b", a, b);
                error_flag = 1;
            end

            // Checking logical OR
            if (out_or_logical !== expected_logical) begin
                $display("Logical OR failed at a=%b, b=%b", a, b);
                error_flag = 1;
            end

            // Checking NOT outputs
            if (out_not !== expected_not) begin
                $display("NOT operation failed at a=%b, b=%b", a, b);
                error_flag = 1;
            end
        end
    endtask

    initial begin
        error_flag = 0;

        // Test cases loop for all possible combinations of a and b
        for (i = 0; i < 8; i = i + 1) begin
            for (j = 0; j < 8; j = j + 1) begin
                a = i; b = j;
                #10;  // Wait time for propagation
                check_outputs;
            end
        end

        // Check for all passing cases
        if (error_flag === 0) begin
            $display("all test cases passed");
        end

        $finish;  // End the simulation
    end

endmodule

