module tb_top_module;

// Inputs
reg clk;
reg in;
reg reset;

// Outputs
wire [7:0] out_byte;
wire done;

// Instantiate the Unit Under Test (UUT)
top_module uut (
    .clk(clk),
    .in(in),
    .reset(reset),
    .out_byte(out_byte),
    .done(done)
);

// Clock generation
initial begin
    clk = 0;
    forever #5 clk = ~clk; // 100 MHz Clock
end

// Stimulus here
initial begin
    // Initialize Inputs
    reset = 1; in = 1;
    #20; // reset the system
    reset = 0;

    // Case 0: Correct byte sequence
    // Start bit (0), 8-bit data (0xAA -> 01010101), Stop bit (1)
    send_byte(8'h55);

    // Case 1: Error in stop bit (0 instead of 1)
    send_byte_error(8'h34, 0);

    // Case 2: Correct byte after an error
    send_byte(8'h78);

    // Case 3: Multiple Correct bytes
    send_byte(8'hAA);
    send_byte(8'hBB);
    send_byte(8'hCC);

    // Finish test, check results
    #40;
    if (test_fail == 0) begin
        $display("All test cases passed.");
    end

    $finish;
end

integer test_fail = 0;
reg [8:0] expected_byte;

task send_byte;
    input [7:0] data_byte;
    begin
        expected_byte = {1'b1, data_byte};
        send_bit_stream(expected_byte, 10);

        if(out_byte !== data_byte || done !== 1) begin
            test_fail = 1;
        end
    end
endtask

task send_byte_error;
    input [7:0] data_byte;
    input stop_bit;
    begin
        expected_byte = {stop_bit, data_byte};
        send_bit_stream(expected_byte, 10);
    end
endtask

task send_bit_stream;
    input [9:0] bits;
    input integer length;
    integer i;
    begin
        for (i = 0; i < length; i = i + 1) begin
            in = bits[i];
            #10;
        end
    end
endtask

endmodule

