module testbench;
    reg clk, areset, x;
    wire z;

    top_module DUT (
        .clk(clk),
        .areset(areset),
        .x(x),
        .z(z)
    );

    initial begin
        // Initialize clock
        clk = 1'b0;
        forever #5 clk = ~clk; // Toggle clock every 5 ns
    end

    // Stimulus for the DUT
    initial begin
        // Apply asynchronous reset
        areset = 1'b1;
        #10 areset = 1'b0;

        // First input sequence (e.g., 3 -> 1101 with LSB first)
        x = 0; #10; // LSB 0
        x = 1; #10; // Next bit
        x = 1; #10; // Next bit
        areset = 1'b1; #10; // reset on
        #10 areset = 1'b0; 

        // Second input sequence, negative test (e.g., 6 -> 1010 with LSB first)
        x = 0; #10;
        x = 1; #10;
        x = 1; #10;
        x = 0; #10;
        areset = 1'b1; #10;
        x = 1; // Random input during reset to check proper reset behavior
        #10 areset = 1'b0;

        // More sequences can be added here if needed

        #500; // Wait time after last sequence
        $finish;
    end

    initial begin
        $monitor("Time = %t, x = %b, z = %b", $time, x, z);
    end

    integer test_passed = 1;

    initial begin
        @(posedge clk);
        wait(areset == 0); // Wait for the first reset release
        @(posedge clk);
        if (z !== 1'b0) test_passed = 0; // Check first output against expected

        @(posedge clk);
        if (z !== 1'b1) test_passed = 0; // Check second output

        @(posedge clk);
        if (z !== 1'b0) test_passed = 0; // Check third output

        @(posedge clk);
        @(posedge clk); // Skip one more clock cycle for the next sequence
        @(posedge clk);
        if (z !== 1'b0) test_passed = 0;

        @(posedge clk);
        if (z !== 1'b1) test_passed = 0;

        @(posedge clk);
        if (z !== 1'b0) test_passed = 0;

        if(test_passed == 1)
            $display("all test cases passed");
    end
