module testbench;

    // Inputs
    reg clk;
    reg reset;
    reg data;

    // Outputs
    wire start_shifting;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .clk(clk),
        .reset(reset),
        .data(data),
        .start_shifting(start_shifting)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // 100 MHz clock
    end

    // Test Cases
    initial begin
        // Initialize Inputs
        reset = 1; data = 0;
        #10 reset = 0;

        // CASE 1: Check reset functionality
        if (start_shifting !== 0) begin
            $display("Reset Test Failed");
            $finish;
        end

        // Send the sequence 1101
        data = 1;
        repeat(2) @(negedge clk);
        data = 0;
        @(posedge clk);
        data = 1;
        @(posedge clk);

        @(negedge clk);  // Check one cycle after the sequence
        if (start_shifting !== 1) begin
            $display("Sequence detection failed");
            $finish;
        end

        // Ensure it stays high
        repeat(10) @(posedge clk);
        if (start_shifting !== 1) begin
            $display("Persistence Test Failed");
            $finish;
        end

        // CASE 2: Reset while high
        reset = 1;
        @(posedge clk);
        reset = 0;
        if (start_shifting !== 0) begin
            $display("Reset while high Test Failed");
            $finish;
        end

        // Re-test sequence detection
        data = 1;
        repeat(2) @(negedge clk);
        data = 0;
        @(posedge clk);
        data = 1;
        @(posedge clk);

        @(negedge clk);  // Check one cycle after the sequence
        if (start_shifting !== 1) begin
            $display("Re-Sequence detection failed");
            $finish;
        end

        // Check persistence again
        repeat(10) @(posedge clk);
        if (start_shifting !== 1) begin
            $display("Persistence after re-activation Test Failed");
            $finish;
        end

        $display("all test cases passed");
        $finish;
    end
endmodule

