module testbench;
    reg clock, a;
    reg p, q;
    wire p_dut, q_dut;

    // Instantiate the Device Under Test (DUT)
    top_module DUT(
        .clock(clock),
        .a(a),
        .p(p_dut),
        .q(q_dut)
    );

    // Clock generation
    initial begin
        clock = 0;
        forever #5 clock = !clock;
    end

    // Initialize input signals and monitors
    initial begin
        a = 0;
        p = 'bx;
        q = 'bx;
        // Display signals and compare with expected outputs
        $monitor("time = %t, clock = %b, a = %b, p = %b (%b), q = %b (%b)",
                 $time, clock, a, p_dut, p, q_dut, q);
    end

    // Test stimulus
    initial begin
        #25 a = 0; p = 0;       // Expected
        #30;                    
        #5 a = 1;               // Expected p should remain 0
        #10 a = 0;              // Check stability
        #5 a = 1;
        #5 a = 0;
        #5 a = 1;
        #5 a = 0;
        #10 a = 0; p = 1; q = 1; // Expected
        #5 a = 1; 
        #5 a = 0;
        #5 a = 1;
        #5;
        #100; // Wait longer for additional checks (q should follow p after a specific pattern with delay)
    end

    // Check the expected p and q values at specific times
    initial begin
        #55 if (p_dut !== 0 || q_dut !== 0) $display("Test failed at 55ns");
        #90 if (p_dut !== 1 || q_dut !== 0) $display("Test failed at 90ns");
        #115 if (p_dut !== 1 || q_dut !== 1) $display("Test failed at 115ns");
        #145 if (p_dut !== 0 || q_dut !== 1) $display("Test failed at 145ns");
        #170 if (p_dut !== 1 || q_dut !== 1) $display("Test failed at 170ns");
        #175 if (p_dut !== 0 || q_dut !== 0) $display("Test failed at 175ns");
    end

    // Check that all conditions pass
    reg failed = 0;
    always @(p_dut, q_dut) begin
        if (($time == 55 && (p_dut !== 0 || q_dut !== 0)) ||
            ($time == 90 && (p_dut !== 1 || q_dut !== 0)) ||
            ($time == 115 && (p_dut !== 1 || q_dut !== 1)) ||
            ($time == 145 && (p_dut !== 0 || q_dut !== 1)) ||
            ($time == 170 && (p_dut !== 1 || q_dut !== 1)) ||
            ($time == 175 && (p_dut !== 0 || q_dut !== 0))) begin
                failed = 1;
        end
    end

    initial begin
        #200;
        if (!failed)
            $display("all test cases passed");
        $finish;
    end
endmodule

