module testbench;

    // Inputs
    reg a;
    reg b;
    reg c;
    reg d;

    // Outputs
    wire out_sop;
    wire out_pos;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .a(a), 
        .b(b), 
        .c(c), 
        .d(d), 
        .out_sop(out_sop), 
        .out_pos(out_pos)
    );

    // Variable to keep track of passed tests
    integer passed_tests = 0;

    // Test vector structure
    reg [3:0] test_vectors[15:0]; // 4 bits for a, b, c, d
    reg expected_sop[15:0];
    reg expected_pos[15:0];

    initial begin
        // Initialize test vectors and expected results
        test_vectors[0] = 4'b0000; expected_sop[0] = 0; expected_pos[0] = 0;
        test_vectors[1] = 4'b0001; expected_sop[1] = 0; expected_pos[1] = 0;
        test_vectors[2] = 4'b0010; expected_sop[2] = 1; expected_pos[2] = 1;
        test_vectors[3] = 4'b0011; expected_sop[3] = 0; expected_pos[3] = 0; // undefined, assuming 0
        test_vectors[4] = 4'b0100; expected_sop[4] = 0; expected_pos[4] = 0;
        test_vectors[5] = 4'b0101; expected_sop[5] = 0; expected_pos[5] = 0;
        test_vectors[6] = 4'b0110; expected_sop[6] = 0; expected_pos[6] = 0;
        test_vectors[7] = 4'b0111; expected_sop[7] = 1; expected_pos[7] = 1;
        test_vectors[8] = 4'b1000; expected_sop[8] = 0; expected_pos[8] = 0; // undefined, assuming 0
        test_vectors[9] = 4'b1001; expected_sop[9] = 0; expected_pos[9] = 0;
        test_vectors[10] = 4'b1010; expected_sop[10] = 0; expected_pos[10] = 0;
        test_vectors[11] = 4'b1011; expected_sop[11] = 0; expected_pos[11] = 0; // undefined, assuming 0
        test_vectors[12] = 4'b1100; expected_sop[12] = 0; expected_pos[12] = 0; // undefined, assuming 0
        test_vectors[13] = 4'b1101; expected_sop[13] = 0; expected_pos[13] = 0;
        test_vectors[14] = 4'b1110; expected_sop[14] = 0; expected_pos[14] = 0;
        test_vectors[15] = 4'b1111; expected_sop[15] = 1; expected_pos[15] = 1;

        // Testing loop
        for (integer i = 0; i < 16; i = i + 1) begin
            {a, b, c, d} = test_vectors[i];
            #10; // delay for signals to stabilize

            if (out_sop === expected_sop[i] && out_pos === expected_pos[i])
                passed_tests = passed_tests + 1;
            else begin
                $display("Test case %d failed: inputs = %b, expected out_sop = %b, expected out_pos = %b, got out_sop = %b, got out_pos = %b.",
                    i, test_vectors[i], expected_sop[i], expected_pos[i], out_sop, out_pos);
            end
        end

        if (passed_tests == 16) 
            $display("all test cases passed");

        $finish;
    end

endmodule

