        #10;
        total_tests = total_tests + 1;
        if (s == 8'b00001001 && overflow == 0) passed_tests = passed_tests + 1; // Expecting 9, no overflow

        // Test Case 5: Negative Addition
        a = 8'b11111011; // -5
        b = 8'b11111101; // -3
        #10;
        total_tests = total_tests + 1;
        if (s == 8'b11111000 && overflow == 0) passed_tests = passed_tests + 1; // Expecting -8, no overflow

