    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock period of 10ns
    end

    // Test vectors
    initial begin
        // Initial Reset
        aresetn = 0;
        x = 0;
        #10; // Wait for the reset
        aresetn = 1;
        #10;

        // Test sequence
        @(negedge clk) x = 1;
        @(negedge clk) x = 0;
        @(negedge clk) x = 1;
        if (z !== 1) $display("Test case failed");

        // Check for overlapping sequences
        @(negedge clk) x = 0;
        @(negedge clk) x = 1;
        if (z !== 1) $display("Test case failed");

        // Further non-overlapping
        @(negedge clk) x = 0;
