        @(negedge clk) x = 0;
        if (z !== 0) $display("Test case failed");
        @(negedge clk) x = 0;
        if (z !== 0) $display("Test case failed");
        @(negedge clk) x = 0;
        if (z !== 0) $display("Test case failed");

        // Final check to display all test cases passed
