module testbench;
    // Inputs
    reg clk;
    reg reset;

    // Outputs
    wire [3:0] q;

    // Initialize the DUT
    top_module DUT (
        .clk(clk),
        .reset(reset),
        .q(q)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // 100MHz clock
    end

    // Test cases
    integer i;
    reg test_failed;

    initial begin
        test_failed = 0;
        reset = 1; // Apply reset
        #10;       // Wait for reset to take effect
        reset = 0;

        // Checking reset functionality
        if (q !== 0) begin
            test_failed = 1;
            $display("Test case failed: Counter did not reset to 0");
        end

        // Check counting sequence
        for (i = 0; i < 20; i = i + 1) begin
            #10; // Wait for next clock edge

            if (i < 10 && q !== i) begin
                test_failed = 1;
                $display("Test case failed at count %d, expected %d, got %d", i, i, q);
            end

            if (i >= 10 && q !== (i - 10)) begin
                test_failed = 1;
                $display("Test case failed at count %d, expected %d, got %d", i, i - 10, q);
            end
        end

        // Check synchronous reset at a mid-count
        @(posedge clk);
        reset = 1;
        @(posedge clk);
        reset = 0;

        if (q !== 0) begin
            test_failed = 1;
            $display("Test case failed: Counter did not reset to 0 on synchronous reset");
        end

        // Final check: should pass all tests to display "all test cases passed"
        if (test_failed == 0) begin
            $display("all test cases passed");
        end

        $finish;
    end
endmodule

