    top_module dut1 (.clk(clk), .L(L), .q_in(q_out[1]), .r_in(r_in[1]), .Q(q_out[1]));
    top_module dut2 (.clk(clk), .L(L), .q_in(q_out[2]), .r_in(r_in[2]), .Q(q_out[2]));

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Toggle every 5ns
    end

    // Test cases
