        L = 1'b1;
        r_in = 3'b101;
        #10; // wait for a clock cycle
        expected_q = 3'b101;
        check_results(expected_q, "Load functionality test");

        // Test Case 2: Shift functionality
        L = 1'b0;
        #10; // wait for a clock cycle to shift
        expected_q = {r_in[1] ^ r_in[2], r_in[0], r_in[2]};
