module tb_shift_register;

    reg clk;
    reg load;
    reg ena;
    reg [1:0] amount;
    reg [63:0] data;
    wire [63:0] q;

    top_module uut (
        .clk(clk),
        .load(load),
        .ena(ena),
        .amount(amount),
        .data(data),
        .q(q)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // Clock with period 10 ns
    end

    // Test Procedure
    initial begin
        // Initialize signals
        load = 0;
        ena = 0;
        amount = 0;
        data = 0;

        // Wait for the global reset
        #15;

        // Test Case 1: Load the register
        load = 1;
        data = 64'hA5A5_A5A5_A5A5_A5A5;
        #10;
        load = 0;
        if (q !== 64'hA5A5_A5A5_A5A5_A5A5) begin
            $display("Test Case 1 Failed: Load operation not successful.");
            $finish;
        end

        // Test Case 2: Shift left by 1
        ena = 1;
        amount = 2'b00;
        #10;
        if (q !== {data[62:0], 1'b0}) begin
            $display("Test Case 2 Failed: Shift left by 1 not successful.");
            $finish;
        end

        // Test Case 3: Shift left by 8
        amount = 2'b01;
        #10;
        if (q !== {data[55:0], 8'b0}) begin
            $display("Test Case 3 Failed: Shift left by 8 not successful.");
            $finish;
        end

        // Test Case 4: Shift right by 1 (arithmetic)
        ena = 1;
        amount = 2'b10;
        #10;
        if(q[63] != q[62] || q[62:0] !== data[63:1]) begin
            $display("Test Case 4 Failed: Arithmetic Shift right by 1 not successful.");
            $finish;
        end

        // Test Case 5: Shift right by 8 (arithmetic)
        amount = 2'b11;
