module testbench;
    reg in;
    reg [1:0] state;
    wire [1:0] next_state;
    wire out;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .in(in),
        .state(state),
        .next_state(next_state),
        .out(out)
    );

    // Test Cases Variables
    integer errors = 0;
    reg [1:0] expected_next_state;
    reg expected_out;

    // Procedure to apply test case and check the results
    task check_case;
        input reg in_test;
        input reg [1:0] state_test;
        input reg [1:0] expected_ns;
        input reg expected_o;
        begin
            in = in_test;
            state = state_test;
            #10; // wait for the output to settle

            if (next_state !== expected_ns || out !== expected_o) begin
                errors = errors + 1;
                $display("Error: Input = %b, State = %b | Expected: next_state = %b, out = %b | Received: next_state = %b, out = %b",
                        in, state, expected_ns, expected_o, next_state, out);
            end
        end
    endtask

    // Initial block to start the tests
    initial begin
        // Test case 1: A, in = 0 -> (next state = A, out = 0)
        check_case(0, 2'b00, 2'b00, 0);

        // Test case 2: A, in = 1 -> (next state = B, out = 0)
        check_case(1, 2'b00, 2'b01, 0);

        // Test case 3: B, in = 0 -> (next state = C, out = 0)
        check_case(0, 2'b01, 2'b10, 0);

        // Test case 4: B, in = 1 -> (next state = B, out = 0)
        check_case(1, 2'b01, 2'b01, 0);

        // Test case 5: C, in = 0 -> (next state = A, out = 0)
        check_case(0, 2'b10, 2'b00, 0);

        // Test case 6: C, in = 1 -> (next state = D, out = 0)
        check_case(1, 2'b10, 2'b11, 0);

        // Test case 7: D, in = 0 -> (next state = C, out = 1)
        check_case(0, 2'b11, 2'b10, 1);

        // Test case 8: D, in = 1 -> (next state = B, out = 1)
        check_case(1, 2'b11, 2'b01, 1);

        // Display result based on the number of errors found
        if (errors == 0) begin
            $display("all test cases passed");
        end

        $finish;
    end
endmodule

