        end
        if (out_different[99] !== 1'b0) $display("Test case failed");

        // Test Case 2: All ones
        in = 100'b1;
        #10;
        for (i = 0; i < 99; i++) begin
            if (out_both[i] !== 1'b1) $display("Test case failed");
            if (out_different[i] !== 1'b0) $display("Test case failed");
