            if (out_any[i] !== 1'b1) $display("Test case failed");
        end
        if (out_different[99] !== 1'b0) $display("Test case failed");

        // Test Case 3: Alternate zeros and ones
        in = 100'b1010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101;
        #10;
        for (i = 0; i < 99; i++) begin
            if (out_both[i] !== 1'b0) $display("Test case failed");
            if (out_different[i] !== (i % 2 == 0 ? 1'b1 : 1'b0)) $display("Test case failed");
