module top_module_tb;

    // Inputs
    reg clk;
    reg reset;
    reg data;
    reg done_counting;
    reg ack;

    // Outputs
    wire shift_ena;
    wire counting;
    wire done;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .clk(clk),
        .reset(reset),
        .data(data),
        .shift_ena(shift_ena),
        .counting(counting),
        .done_counting(done_counting),
        .done(done),
        .ack(ack)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // 100MHz Clock
    end

    // Apply inputs
    initial begin
        // Initialize Inputs
        reset = 1;
        data = 0;
        done_counting = 0;
        ack = 0;

        // Reset
        #10;
        reset = 0;
        #10;
        reset = 1;
        #10;
        reset = 0;

        // Start simulation
        // Write the detection sequence 1101 and check shift_ena
        send_bits(4'b1101);  // sequence to start timer
        check_shift_ena();   // should enable shift_ena for 4 clock cycles

        // Send duration bits and check counting
        send_bits(4'b1010);  // arbitrary duration bits
        #20;
        assert(counting) else $display("Test case failed - counting not asserted correctly");

        // Simulate counting done
        done_counting = 1;
        #10;
        assert(done) else $display("Test case failed - done not asserted correctly");

        // Acknowledge the timer
        ack = 1;
        #10;
        ack = 0;

        // Make sure system resets correctly
        if (shift_ena || counting || done)
            $display("Test case failed - outputs did not reset correctly");
        else
            $display("all test cases passed");

        // End simulation
        $finish;
    end

    // Helper task to send bits
    task send_bits;
        input [3:0] bits;
        integer i;
        begin
            for (i = 3; i >= 0; i = i - 1) begin
                data = bits[i];
                #10; // wait for one clock cycle
            end
        end
    endtask

    // Helper task to check shift_ena
    task check_shift_ena;
        integer i;
        begin
            for (i = 0; i < 4; i = i + 1) begin
                #10; // wait for one clock cycle
                if (!shift_ena) begin
                    $display("Test case failed - shift_ena not asserted correctly");
                    disable check_shift_ena;
                end
            end
            #10;
            if (shift_ena)
                $display("Test case failed - shift_ena not deasserted correctly");
        end
    endtask

endmodule

