module testbench;
    // Inputs
    reg a;
    reg b;
    reg c;
    reg d;

    // Output
    wire out;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .a(a), 
        .b(b), 
        .c(c), 
        .d(d), 
        .out(out)
    );

    // Variables for testbench
    integer errors = 0;  // Error counter
    integer i;

    // Test vectors based on the Karnaugh map
    reg [3:0] test_vectors[15:0];  // 4 bits for a, b, c, d
    initial begin
        // Test vector assignments based on the Karnaugh map
        test_vectors[0]  = 4'b0000; // abcd = 0000 -> out = 0
        test_vectors[1]  = 4'b0001; // abcd = 0001 -> out = 1
        test_vectors[2]  = 4'b0011; // abcd = 0011 -> out = 0
        test_vectors[3]  = 4'b0010; // abcd = 0010 -> out = 1
        test_vectors[4]  = 4'b0100; // abcd = 0100 -> out = 1
        test_vectors[5]  = 4'b0101; // abcd = 0101 -> out = 0
        test_vectors[6]  = 4'b0111; // abcd = 0111 -> out = 1
        test_vectors[7]  = 4'b0110; // abcd = 0110 -> out = 0
        test_vectors[8]  = 4'b1100; // abcd = 1100 -> out = 0
        test_vectors[9]  = 4'b1101; // abcd = 1101 -> out = 1
        test_vectors[10] = 4'b1111; // abcd = 1111 -> out = 0
        test_vectors[11] = 4'b1110; // abcd = 1110 -> out = 1
        test_vectors[12] = 4'b1000; // abcd = 1000 -> out = 1
        test_vectors[13] = 4'b1001; // abcd = 1001 -> out = 0
        test_vectors[14] = 4'b1011; // abcd = 1011 -> out = 1
        test_vectors[15] = 4'b1010; // abcd = 1010 -> out = 0

        // Testing loop
        for (i = 0; i < 16; i = i + 1) begin
          a = test_vectors[i][3];
          b = test_vectors[i][2];
          c = test_vectors[i][1];
          d = test_vectors[i][0];
          #10; // Wait for the outputs to settle

          // Determine expected output from the bit position in the loop
          if (out !== test_vectors[i][1]) begin
            errors = errors + 1;
          end
        end

        // Results
        if (errors == 0) 
            $display("all test cases passed");
    end
endmodule

