        .bump_right(bump_right),
        .ground(ground),
        .dig(dig),
        .walk_left(walk_left),
        .walk_right(walk_right),
        .aaah(aaah),
        .digging(digging)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock period of 10ns
    end

    // Test scenarios
    initial begin
        // Initialize all inputs
        areset = 0; bump_left = 0; bump_right = 0; ground = 1; dig = 0;

        // Assert reset
        #10 areset = 1;
        #10 areset = 0;

        // Case 1: Basic walking left to right transition via bump
        bump_left = 1;
        #10;
        if (!walk_right) $display ("Test case failed.");
        bump_left = 0; bump_right = 0;

        // Case 2: Basic walking right to left transition via bump
        bump_right = 1;
        #10;
        if (!walk_left) $display ("Test case failed.");
