        dig = 1;
        #10;
        if (!digging) $display ("Test case failed.");
        ground = 0;
        #10;
        if (!aaah) $display ("Test case failed.");
        dig = 0;
        ground = 1;
        #10;
        if (!((aaah == 0) && (walk_left || walk_right))) $display ("Test case failed.");

        // Case 5: Fall for too long (more than 20 cycles) and then splatter
        ground = 0;
        #210 // Falling for 21 clock cycles (21 * 10ns)
        ground = 1;
        #10;
        if (walk_left || walk_right || aaah || digging) $display ("Test case failed.");

        #210 // Additional time for checking stable state after splatter
        if (walk_left || walk_right || aaah || digging) $display ("Test case failed.");

