module testbench;

    // Input signals
    reg clk;
    reg [7:0] d;
    reg areset;

    // Output signals
    wire [7:0] q;

    // Instantiate the Device Under Test (DUT)
    top_module dut(
        .clk(clk),
        .d(d),
        .areset(areset),
        .q(q)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Generate a clock with 10 ns period
    end

    // Test cases
    initial begin
        // Initialize inputs
        d = 8'b0;
        areset = 0;

        // Apply asynchronous reset
        #10 areset = 1;
        #10 areset = 0;

        // Check if reset works
        if (q !== 8'b00000000) begin
            $display("Test case failed: Reset to 0");
            $finish;
        end

        // Test data loading onto the flip-flops
        #10 d = 8'b10101010; // Input pattern
        #10;
        if (q !== 8'b10101010) begin
            $display("Test case failed: Data pattern 10101010");
            $finish;
        end

        #10 d = 8'b01010101; // Another pattern
        #10;
        if (q !== 8'b01010101) begin
            $display("Test case failed: Data pattern 01010101");
            $finish;
        end

        // Re-test reset after operating
        #10 areset = 1;
        #10 areset = 0;

        #10;
        if (q !== 8'b00000000) begin
            $display("Test case failed: Re-reset to 0");
            $finish;
        end

        // All test cases are passed if no failures
        $display("all test cases passed");
        $finish;
    end

endmodule

