module testbench;
    // Testbench Input Regs
    reg [4:0] a, b, c, d, e, f;

    // Testbench Output Wires
    wire [7:0] w, x, y, z;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .a(a),
        .b(b),
        .c(c),
        .d(d),
        .e(e),
        .f(f),
        .w(w),
        .x(x),
        .y(y),
        .z(z)
    );

    // Test Vectors
    reg [29:0] input_vector;
    reg [31:0] expected_output;

    // Variable to track testing state
    reg passed;

    // Test procedure
    initial begin
        // Initialize passed to true
        passed = 1;

        // Vector 1
        a = 5'b00001; b = 5'b00010; c = 5'b00100; d = 5'b01000; e = 5'b10000; f = 5'b11111;
        input_vector = {a, b, c, d, e, f};
        expected_output = {input_vector, 2'b11};
        #10;
        if ({w, x, y, z} !== expected_output) passed = 0;

        // Vector 2
        a = 5'b11111; b = 5'b00000; c = 5'b11111; d = 5'b00000; e = 5'b11111; f = 5'b00000;
        input_vector = {a, b, c, d, e, f};
        expected_output = {input_vector, 2'b11};
        #10;
        if ({w, x, y, z} !== expected_output) passed = 0;

        // Vector 3
        a = 5'b10101; b = 5'b01010; c = 5'b10101; d = 5'b01010; e = 5'b10101; f = 5'b01010;
        input_vector = {a, b, c, d, e, f};
        expected_output = {input_vector, 2'b11};
        #10;
        if ({w, x, y, z} !== expected_output) passed = 0;

        // Vector 4
        a = 5'b11011; b = 5'b00100; c = 5'b11011; d = 5'b00100; e = 5'b11011; f = 5'b00100;
        input_vector = {a, b, c, d, e, f};
        expected_output = {input_vector, 2'b11};
        #10;
        if ({w, x, y, z} !== expected_output) passed = 0;

        // Display outcome
        if (passed) $display("all test cases passed");

        // Terminate simulation
        $finish;
    end
endmodule

