module testbench;
    // Inputs
    reg clk;
    reg a;
    reg b;

    // Outputs
    wire q;
    wire state;

    // Instantiate the Unit Under Test (DUT)
    top_module uut (
        .clk(clk),
        .a(a),
        .b(b),
        .q(q),
        .state(state)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;
    end

    // Initial block to test various cases
    initial begin
        // Initialize Inputs
        a = 0;
        b = 0;

        // Wait for global reset
        #1;

        // Case: 0
        #4; a = 1; // At time 5ns
        // Case: 1
        #5; a = 0; b = 0; // At time 10ns
        #5; // At time 15ns
        // Case: 2
        #5; // At time 20ns, all same
        #5; // At time 25ns, all same
        #5; // At time 30ns, all same
        #5; // At time 35ns, all same
        // Case: 3
        #5; b = 1; // At time 40ns
        #5; a = 1; b = 0; // At time 45ns
        #5; // At time 50ns same
        #5; a = 1; b = 1; // Time 55ns
        #5; // At time 60ns same
        #5; a = 0; b = 0; // At time 65ns
        #5; // At time 70ns same
        #5; // At time 75ns same
        #5; // At time 80ns same
        #5; a = 1; b = 1; // At time 85ns
        #5; // At time 90ns same
        #5; // At time 95ns same
        #5; a = 1; b = 0; // At time 100ns
        #5; // At time 105ns same
        #5; a = 0; b = 1; // At time 110ns
        #5; // At time 115ns same
        #5; a = 0; b = 0; // At time 120ns
        #5; // At time 125ns same
        #5; // At time 130ns same
        #5; // At time 135ns same
        #5; // At time 140ns same
        #5; // At time 145ns same
        #5; // At time 150ns same
        #5; // At time 155ns same
        #5; // At time 160ns same

        #10; a = 0; b = 0; // Final resting state check at 170ns, 175ns

        // Checking Output
        #10; // Checking at final 180ns, 185ns
        if (state == q) begin
            $display("all test cases passed");
        end

        // Finish simulation
        #5;
        $finish;
    end
endmodule

