module testbench;
    reg clk, reset;
    wire shift_ena;

    top_module DUT (
        .clk(clk),
        .reset(reset),
        .shift_ena(shift_ena)
    );

    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Generate clock with 10 ns period
    end

    initial begin
        // Initialize signals
        reset = 0;
        @(posedge clk);
        @(posedge clk);
        reset = 1; // Apply reset
        @(posedge clk);
        reset = 0;

        // Check shift_ena only active for 4 cycles
        @(posedge clk);
        if (shift_ena !== 1) $display("Test Case Failed");
        @(posedge clk);
        if (shift_ena !== 1) $display("Test Case Failed");
        @(posedge clk);
        if (shift_ena !== 1) $display("Test Case Failed");
        @(posedge clk);
        if (shift_ena !== 1) $display("Test Case Failed");
        @(posedge clk);
        if (shift_ena !== 0) $display("Test Case Failed");
        @(posedge clk);
        if (shift_ena !== 0) $display("Test Case Failed");

        // Test that shift_ena remains 0 after first 4 cycles post-reset
        repeat (10) @(posedge clk);
        if (shift_ena !== 0) $display("Test Case Failed");

        // Resetting again to check reset behavior
        @(posedge clk);
        reset = 1;
        @(posedge clk);
        reset = 0;

        @(posedge clk);
        if (shift_ena !== 1) $display("Test Case Failed");
        @(posedge clk);
        if (shift_ena !== 1) $display("Test Case Failed");
        @(posedge clk);
        if (shift_ena !== 1) $display("Test Case Failed");
        @(posedge clk);
        if (shift_ena !== 1) $display("Test Case Failed");
        @(posedge clk);
        if (shift_ena !== 0) $display("Test Case Failed");

        repeat (10) @(posedge clk);
        if (shift_ena !== 0) $display("Test Case Failed");
        else $display("all test cases passed");

        // Finish simulation
        $finish;
    end
endmodule

